function emotepanel:OnInitialize()
	self.db = LibStub("AceDB-3.0"):New("EmotePanelDB", emotepanel.defaults)
	LibStub("AceConfig-3.0"):RegisterOptionsTable("EmotePanel", emotepanel.options)
	LibStub("AceConfigDialog-3.0"):AddToBlizOptions("EmotePanel")

	SLASH_EMOTEPANEL1 = "/emotepanel"
	SLASH_EMOTEPANEL2 = "/ep"
	SlashCmdList["EMOTEPANEL"] = function(msg)
		showEmotePanel()
	 end 
end


function doEmote(button)
	local emoteCmd = '/' .. button:GetText():upper();
	DEFAULT_CHAT_FRAME.editBox:SetText(emoteCmd) ChatEdit_SendText(DEFAULT_CHAT_FRAME.editBox, 0)
end

function showEmotePanel()

	if areEmotesLoaded then
		local oldButtons = { emotepanel_main_frame:GetChildren() }

		for key,button in ipairs(oldButtons) do
			if button:GetName() then
				button:Hide()
			end
		end
	end

	renderEmotes()

	areEmotesLoaded = true;
	emotepanel_main_frame:Show();

end

