function createEmoteButton(name, x,y)
	local mybutton = CreateFrame("Button",name,emotepanel_main_frame,"UIPanelButtonTemplate")
	mybutton:SetPoint("TOP",0,y)
	mybutton:SetPoint("LEFT",x,0)
	mybutton:SetWidth(80)
	mybutton:SetHeight(22)
	mybutton:SetText(name)
	mybutton:SetScript("OnClick", function(self, arg1)
		doEmote(mybutton);
	end)
end

